import { Drawer, Hidden, IconButton, List, ListItem } from "@material-ui/core";
import { Menu } from "@material-ui/icons";
import React from "react";
import Layout from "../components/shared/Layout";
import { useEditProfilePageStyles } from "../styles";

function EditProfilePage({ history }) {
  const classes = useEditProfilePageStyles();
  const [showDrawer, setDrawer] = React.useState(false)

  function handleToggleDrawer() {
    setDrawer(prev => !prev);
  }

  function handleSelected(index) {
    const path = history.location.pathname
    switch(index) {
      case 0:
        return history.location.pathname.includes('edit')
      default:
        break;
    }
  }

  const options = [
    "Edit Profile",
    "Change Password",
    "Apps and Websites",
    "Email and SMS",
    "Push Notifications",
    "Manage Contacts ",
    "Privacry and Security",
    "Login Activity",
    "Emails from Instagram"
  ]

  const drawer = (
    <List>
      {options.map(option => (
        <ListItem>
          {options.map((option, index) => (
            <ListItem
              key={option}
              button
              selected={handleSelected(index)}
            >
            </ListItem>
          ))}
        </ListItem>
      ))}
    </List>
  )

  return (
    <Layout title="Edit Profile">
      <section className={classes.section}>
        <IconButton edge="start"
          onClick={handleToggleDrawer}
          className={classes.menuButton}
        >
          <Menu />
        </IconButton>
        <nav>
          <Hidden smUp implmenetation="css">
            <Drawer
              variant="temporary"
              anchor="left"
              open ={ showDrawer }
              onClose ={handleToggleDrawer}
              classes={{ paperAnchorDockedLeft: classes.temporaryDrawer }}
            >

            </Drawer>

          </Hidden>
        </nav>
      </section>
    </Layout>
    )
}

export default EditProfilePage;
