import { Drawer, Hidden, IconButton, List, ListItem, ListItemText } from "@material-ui/core";
import { Menu } from "@material-ui/icons";
import React from "react";
import Layout from "../components/shared/Layout";
import { defaultCurrentUser } from "../data";
import { useEditProfilePageStyles } from "../styles";

function EditProfilePage({ history }) {
  const classes = useEditProfilePageStyles();
  //history used for the conditional edit form that will appear on the edit page
  const path = history.location.pathname
  const [showDrawer, setDrawer] = React.useState(false)

  function handleToggleDrawer() {
    setDrawer(prev => !prev);
  }

  function handleSelected(index) {

    switch(index) {
      case 0:
        return history.location.pathname.includes('edit')
      default:
        break;
    }
  }

function handleListClick(index) {
  switch (index) {
    case 0:
      history.push('/accounts/edit');
      break;
    default:
      break;
  }
}

  const options = [
    "Edit Profile",
    "Change Password",
    "Apps and Websites",
    "Email and SMS",
    "Push Notifications",
    "Manage Contacts ",
    "Privacry and Security",
    "Login Activity",
    "Emails from Instagram"
  ]

  const drawer = (
    <List>
      {options.map(option => (
        <ListItem>
          {options.map((option, index) => (
            <ListItem
              key={option}
              button
              selected={handleSelected(index)}
              onClick={() => handleListClick(index) }
              classes={{
                selected: classes.listItemSelected,
                button: classes.listItemButton
              }}
            >
              <ListItemText primary={option} />
            </ListItem>
          ))}
        </ListItem>
      ))}
    </List>
  )

  return (
    <Layout title="Edit Profile">
      <section className={classes.section}>
        <IconButton edge="start"
          onClick={handleToggleDrawer}
          className={classes.menuButton}
        >
          <Menu />
        </IconButton>
        <nav>
          <Hidden smUp implmenetation="css">
            <Drawer
              variant="temporary"
              anchor="left"
              open ={ showDrawer }
              onClose ={handleToggleDrawer}
              classes={{ paperAnchorDockedLeft: classes.temporaryDrawer }}
            >
              {drawer}
            </Drawer>

          </Hidden>
          <Hidden xsDown implmenetation="css"
            className={classes.permanentDrawerRoot}
          >
            <Drawer
              variant="permanent"
              open
              classes={{ 
                paper: classes.permanentDrawerPaper, 
                root: classes.permanentDrawerRoot
               }}
            >
              {drawer}
            </Drawer>

          </Hidden>
        </nav>
        <main>
          {path.includes('edit') && <EditUserInfo user={defaultCurrentUser} />}
        </main>
      </section>
    </Layout>
    )
}

function EditUserInfo() {
  return <>EditUserInfo</>
}

export default EditProfilePage;
