import React from "react";
import Layout from "../components/shared/Layout";
import { useProfilePageStyles } from "../styles";
import { defaultCurrentUser } from '../data'
import { Card, CardContent, Hidden } from "@material-ui/core";
import ProfilePicture from '../components/shared/ProfilePicture'

function ProfilePage() {
  const isOwner = true ;
  const classes = useProfilePageStyles();

  return <Layout title={`${defaultCurrentUser.name} (@${defaultCurrentUser.username})`}>
      <div>
        {/* hidden component from xs screens and downard (not shown at all) */}
        <Hidden xsDown>
          <Card className={classes.cardLarge}>
            <ProfilePicture isOwner={isOwner} />
            <CardContent className={classes.cardContentLarge}>
              <ProfileNameSection />
              <PostCountSection />
              <NameBioSection />
            </CardContent>

          </Card>
        </Hidden>

        <Hidden smUp>
          <Card className={classes.cardSmall}>
            <CardContent>
              <section className={classes.sectionSmall}>
                <ProfilePicture size = {77} isOwner={isOwner}/>
                <ProfileNameSection />
              </section>
              <NameBioSection />
            </CardContent>
            <PostCountSection />
          </Card>
        </Hidden>
      </div>
    </Layout>;
}
function ProfileNameSection() {
  return <>ProfileNameSection</>
}
function PostCountSection() {
  return <>PostCountSection</>
}
function NameBioSection() {
  return <>ProfileNameBioSection</>
}


export default ProfilePage;
