import React from "react";
import Layout from "../components/shared/Layout";
import { useProfilePageStyles } from "../styles";
import { defaultCurrentUser } from '../data'
import { Avatar, Button, Card, CardContent, Dialog, DialogTitle, Divider, Hidden, Typography, Zoom } from "@material-ui/core";
import ProfilePicture from '../components/shared/ProfilePicture'
import { Link } from "react-router-dom";
import { GearIcon } from "../icons";


function ProfilePage() {
  const isOwner = false ;
  const [showOptionsMenu, setOptionMenu] = React.useState(false)

  function handleOptionsMenuClick() {
    setOptionMenu(true)
  }

  function handleCloseMenu(){
    setOptionMenu(false)
  }
  const classes = useProfilePageStyles();

  return <Layout title={`${defaultCurrentUser.name} (@${defaultCurrentUser.username})`}>
      <div>
        {/* hidden component from xs screens and downard (not shown at all) */}
        <Hidden xsDown>
          <Card className={classes.cardLarge}>
            <ProfilePicture isOwner={isOwner} />
            <CardContent className={classes.cardContentLarge}>
              <ProfileNameSection 
                user={defaultCurrentUser}
                isOwner={isOwner}
                handleOptionsMenuClick={handleOptionsMenuClick}
              />
              <PostCountSection />
              <NameBioSection />
            </CardContent>

          </Card>
        </Hidden>

        <Hidden smUp>
          <Card className={classes.cardSmall}>
            <CardContent>
              <section className={classes.sectionSmall}>
                <ProfilePicture size = {77} isOwner={isOwner}/>
                <ProfileNameSection 
                  user={defaultCurrentUser}
                  isOwner={isOwner}  
                  handleOptionsMenuClick={handleOptionsMenuClick}
                />
              </section>
              <NameBioSection />
            </CardContent>
            <PostCountSection />
          </Card>
        </Hidden>
        {showOptionsMenu && <OptionsMenu handleCloseMenu = {handleCloseMenu} />}
      </div>
    </Layout>;
}
function ProfileNameSection({ user, isOwner, handleOptionsMenuClick}) {
  const classes = useProfilePageStyles()
  const [showUnfollowDialog, setUnfollowDialog] = React.useState(false)

  let followButton;
  const isFollowing = true;
  const isFollower = false;
  if (isFollowing) {
    followButton = (
      <Button variant="outlined" className={classes.button}> 
        Following
      </Button>
    );
  } else if (isFollower) {
    followButton = (
      <Button onClick={() => setUnfollowDialog(true) } variant="contained" color="primary" className={classes.button}>
        Follow Back
      </Button>
    )
  } else {
    followButton = (
      <Button variant="contained" color="primary" className={classes.button}> 
        Follow
      </Button>
    );
  }
  return (
    <>
      <Hidden xsDown>
        <section className={classes.usernameSection}>
          <Typography className={classes.username}>
            {user.username}
          </Typography>
          {isOwner ? (
            <>
              <Link to="/accounts/edit">
                <Button variant="outlined">
                  Edit Profile
                </Button>
              </Link>
              <div onClick={handleOptionsMenuClick} className={classes.settingsWrapper}>
                <GearIcon className={classes.settings} />
              </div>
            </>
          ): (
            <>
              {followButton}
            </>
          )}
        </section>
      </Hidden>
      <Hidden smUp>
          <section>
            <div className={classes.usernameDivSmall}>
            <Typography className={classes.username}>
            {user.username}
            </Typography>
            {isOwner && (
              <div onClick={handleOptionsMenuClick} className={classes.settingsWrapper}>
              <GearIcon className={classes.settings} />
            </div>
            )}
            </div>
            {isOwner ? (
              <Link to="/accounts/edit" style={{ width: '100%' }}>
              <Button variant="outlined">
                Edit Profile
              </Button>
            </Link>
            ): (
            followButton
            )}
          </section>
      </Hidden>
      {showUnfollowDialog && <UnfollowDialog user={user} onclose={() => setUnfollowDialog(true)} />}
    </>
  )
}

function UnfollowDialog({ onClose, user }) {
  const classes = useProfilePageStyles()

  return (
    <Dialog
      open
      classes={{
        scrollpaper: classes.unfollowDialogScrollPaper
      }}
      onClose
      TransictionComponent={Zoom}
    >
      <div class={classes.wrapper}>
        <Avatar
          src={user.prpfile_image}
          alt={`${user.username}'s avatar`}
          className={classes.avatar}
        />
      </div>
      <Typography align="center" variant="body2" className={classes.unfollowDialogText}>
        Unfollow @ {user.username}?
      </Typography>
      <Divider />
      <Button className={classes.unfollowButton}>
        Unfollow
      </Button>
      <Button onCLick={onClose} className={classes.cancelButton}>
        Cancel
      </Button>
    </Dialog>
  )
}

function PostCountSection() {
  return <>PostCountSection</>
}
function NameBioSection() {
  return <>ProfileNameBioSection</>
}

function OptionsMenu({ handleCloseMenu }) {
  const classes = useProfilePageStyles();
  const [showLogOutMessage, setLogOutMessage] =  React.useState(false)

  function handleLogOutClick() {
    setLogOutMessage(true)
  }

  return (
    <Dialog
      open
      classes={{
        scrollPaper: classes.dialogScrollPaper,
        paper: classes.dialogPaper
      }}
      TransitionComponent={Zoom}
    >
      {
        showLogOutMessage ? (
          <DialogTitle className={classes.dialogTitle}>
            Logging Out
            <Typography color='textSecondary'>
              You need to log back in to continue using Instagram
            </Typography>
          </DialogTitle>
        ):(
          <>
            <OptionsItem text="Change Password" />
            <OptionsItem text="Nametag" />
            <OptionsItem text="Authorized Apps" />
            <OptionsItem text="Notifications" />
            <OptionsItem text="Privacy and Security" />
            <OptionsItem text="Logout" onCLick={handleLogOutClick} />
            <OptionsItem text="Cancel" onCLick={handleCloseMenu}/>
          </>
        )}
    </Dialog>
  )
}

function OptionsItem({ text, onCLick }) {
  return (
    <>
      <Button style={{ padding: '12px 8px' }} onClick={onCLick}>
        {text}
      </Button>
      <Divider />
    </>
  )
}

export default ProfilePage;
